mapper.select:
SELECT



FROM jipiao  a



        and a.id in

        #{item}



        and a.jipiao_name like CONCAT('%',#{params.jipiaoName},'%')


        and a.jipiao_types = #{params.jipiaoTypes}


          and a.jipiao_new_money >= #{params.jipiaoNewMoneyStart}   


          and a.jipiao_new_money <= #{params.jipiaoNewMoneyEnd}   


        and a.jipiao_chufadi like CONCAT('%',#{params.jipiaoChufadi},'%')


        and a.jipiao_mudidi like CONCAT('%',#{params.jipiaoMudidi},'%')


          and UNIX_TIMESTAMP(a.jipiao_time) >= UNIX_TIMESTAMP(#{params.jipiaoTimeStart}) 


          and UNIX_TIMESTAMP(a.jipiao_time) <= UNIX_TIMESTAMP(#{params.jipiaoTimeEnd}) 


          and a.zuowei_number >= #{params.zuoweiNumberStart}   


          and a.zuowei_number <= #{params.zuoweiNumberEnd}   


        and a.zuowei_number = #{params.zuoweiNumber}


        and a.shangxia_types = #{params.shangxiaTypes}


        and a.hangban_types = #{params.hangbanTypes}


          and a.jipiao_delete >= #{params.jipiaoDeleteStart}   


          and a.jipiao_delete <= #{params.jipiaoDeleteEnd}   


        and a.jipiao_delete = #{params.jipiaoDelete}


        and a.jipiao_content like CONCAT('%',#{params.jipiaoContent},'%')



order by a.${params.sort} ${params.order}
